package lib;
public class Lib {
    public static void main(String[] args) {
        // TODO code application logic here
        int altura = 5;
        
        for (int fila = 0; fila < altura; fila++){
            for(int espacio = 0; espacio < (altura - fila - 1); espacio++){System.out.print(" ");}
            for(int asterisco = 0; asterisco < (fila * 2) + 1; asterisco++){System.out.print("*");}
            System.out.println("");
        }
        
        int tronco = 2;
        
        for (int base = 0; base < tronco; base++){
            for(int espacio = 0; espacio < (altura - 2); espacio++){System.out.print(" ");}
            for(int largo = 0; largo < 3; largo++){System.out.print("|");}
            System.out.println("");
        }
    }
}
