package bin;
public class Arbol extends Planta{
    private String variedad;
    private String tronco;
    private double radio;
    private String color;
    private String Hojas;

    public Arbol() {}
    public Arbol(String variedad, String tronco, double radio, String color,
            String Hojas, String nombre, double tallo, boolean hojas, String clima){
        super(nombre, tallo, hojas, clima);
        this.variedad = variedad;
        this.tronco = tronco;
        this.radio = radio;
        this.color = color;
        this.Hojas = Hojas;
    }

    public String getVariedad(){return variedad;}
    public void setVariedad(String variedad){this.variedad = variedad;}
    public String getTronco(){return tronco;}
    public void setTronco(String tronco){this.tronco = tronco;}
    public double getRadio(){return radio;}
    public void setRadio(double radio){this.radio = radio;}
    public String getColor(){return color;}
    public void setColor(String color){this.color = color;}
    public String getHojas(){return Hojas;}
    public void setHojas(String Hojas){this.Hojas = Hojas;}

    @Override
    public void hablar(){System.out.println("Hola, soy un √Årbol");}
}