package bin;
public class Arbusto extends Planta{
    private double ancho;
    private boolean domestico;
    private String variedad;
    private String color;
    private boolean podar;

    public Arbusto(){}
    public Arbusto(double ancho, boolean domestico, String variedad, String color,
            boolean podar, String nombre, double tallo, boolean hojas, String clima){
        super(nombre, tallo, hojas, clima);
        this.ancho = ancho;
        this.domestico = domestico;
        this.variedad = variedad;
        this.color = color;
        this.podar = podar;
    }

    public double getAncho(){return ancho;}
    public void setAncho(double ancho){this.ancho = ancho;}
    public boolean isDomestico(){return domestico;}
    public void setDomestico(boolean domestico){this.domestico = domestico;}
    public String getVariedad(){return variedad;}
    public void setVariedad(String variedad){this.variedad = variedad;}
    public String getColor(){return color;}
    public void setColor(String color){this.color = color;}
    public boolean isPodar(){return podar;}
    public void setPodar(boolean podar){this.podar = podar;}
    
    @Override
    public void hablar(){System.out.println("Hola, soy un arbusto");}   
}