package bin;
public class Flor extends Planta{
    private int petalos;
    private String color;
    private String pistilo;
    private String variedad;
    private String estacion;

    public Flor(){}
    public Flor(int petalos, String color, String pistilo, String variedad,
            String estacion, String nombre, double tallo, boolean hojas, String clima){
        super(nombre, tallo, hojas, clima);
        this.petalos = petalos;
        this.color = color;
        this.pistilo = pistilo;
        this.variedad = variedad;
        this.estacion = estacion;
    }

    public int getPetalos(){return petalos;}
    public void setPetalos(int petalos){this.petalos = petalos;}
    public String getColor(){return color;}
    public void setColor(String color){this.color = color;}
    public String getPistilo(){return pistilo;}
    public void setPistilo(String pistilo){this.pistilo = pistilo;}
    public String getVariedad(){return variedad;}
    public void setVariedad(String variedad){this.variedad = variedad;}
    public String getEstacion(){return estacion;}
    public void setEstacion(String estacion){this.estacion = estacion;}

    @Override
    public void hablar(){System.out.println("Hola, soy una Flor");}
    
}