package bin;
public abstract class Planta{
    private String nombre;
    private double tallo;
    private boolean hojas;
    private String clima;

    public Planta(){}
    public Planta(String nombre, double tallo, boolean hojas, String clima){
        this.nombre = nombre;
        this.tallo = tallo;
        this.hojas = hojas;
        this.clima = clima;
    }

    public String getNombre(){return nombre;}
    public void setNombre(String nombre){this.nombre = nombre;}
    public double getTallo(){return tallo;}
    public void setTallo(double tallo){this.tallo = tallo;}
    public boolean isHojas(){return hojas;}
    public void setHojas(boolean hojas){this.hojas = hojas;}
    public String getClima(){return clima;}
    public void setClima(String clima){this.clima = clima;}
    
    public abstract void hablar();
}