#include <iostream>

using namespace std;

int main(){
    int num = 35;
    int num2 = 20;
    int aux;

    cout << "---Antes---" << endl;
    cout << "el primer numero es: " << num << endl;
    cout << "el segundo numero es: " << num2 << endl;

    aux = num2;
    num2 = num;
    num = aux;

    cout << "---Ahora---" << endl;
    cout << "el primer numero es: " << num << endl;
    cout << "el segundo numero es: " << num2 << endl;

    return 0;
}