package templates;

import java.util.LinkedList;
import java.util.List;

public class Templates {
    public static void main(String[] args) {
        // TODO code application logic here
        List<Person> lista = new LinkedList<Person>();
        
        lista.add(new Person(1, "Juan", 24, "Prada Ospina"));
        lista.add(new Person(2, "Esteban", 25, "Prada Ospina"));
        lista.add(new Person(3, "Juan", 23, "Ospina Moreno"));
        lista.add(new Person(4, "Esteban", 22, "Ospina Moreno"));
        lista.add(0, new Person(0, "Juan Esteban", 21, "Prada Ospina"));
        lista.add(new Person(5, "Juan Esteban", 20, "Ospina Moreno"));
        
        for(Person persona : lista){System.out.println(persona.getNum());}
        for(Person persona : lista){System.out.println(persona.getName());}
        for(Person persona : lista){System.out.println(persona.getLastName());}
        for(Person persona : lista){System.out.println(persona.getAge());}
    }
}