package test;
public class Empleado extends Persona{
    int legado;
    String cargo;
    Double sueldo;

    public Empleado() {}

    public Empleado(int legado, String cargo, Double sueldo, int id, String cc,
            String name, String lastName, String domicilio, String telefono) {
        super(id, cc, name, lastName, domicilio, telefono);
        this.legado = legado;
        this.cargo = cargo;
        this.sueldo = sueldo;
    }

    public int getLegado() {return legado;}
    public void setLegado(int legado) {this.legado = legado;}
    public String getCargo() {return cargo;}
    public void setCargo(String cargo) {this.cargo = cargo;}
    public Double getSueldo() {return sueldo;}
    public void setSueldo(Double sueldo) {this.sueldo = sueldo;}
    
}
