package test;
public class Jefe extends Persona{
    int id_jefe;
    String departamentoJefe;

    public Jefe() {}
    public Jefe(int id_jefe, String departamentoJefe, int id, String cc, String name,
            String lastName, String domicilio, String telefono) {
        super(id, cc, name, lastName, domicilio, telefono);
        this.id_jefe = id_jefe;
        this.departamentoJefe = departamentoJefe;
    }

    public int getId_jefe() {return id_jefe;}
    public void setId_jefe(int id_jefe) {this.id_jefe = id_jefe;}
    public String getDepartamentoJefe() {return departamentoJefe;}
    public void setDepartamentoJefe(String departamentoJefe) {this.departamentoJefe = departamentoJefe;}
    
}
