package utils;

import java.util.Scanner;

public class Utils {
    public static void main(String[] args) {
        // TODO code application logic here
        String matricula = "";
        int servicio, horas, cont1 = 0, cont2 = 0, cont3 = 0;
        double total, dia = 0;
        Scanner teclado = new Scanner (System.in);
        
        while (!matricula.equalsIgnoreCase("fin")){
            System.out.println("Ingrese la matricula de su vehiculo");
            System.out.println("O escriba fin si quiere terminar");
            matricula = teclado.nextLine();
            
            if (!matricula.equalsIgnoreCase("fin")){
                System.out.println("Ingrese el tipo de servicio");
                System.out.println("1 - Por hora");
                System.out.println("2 - Media jornada (5 horas)");
                System.out.println("3 - Jornada completa (10 horas)");
                
                teclado = new Scanner (System.in);
                servicio = teclado.nextInt();
            
                if (servicio < 0 || servicio > 3){System.out.println("Ingreso un tipo de servicio incorrecto");}
                else{
                    switch (servicio){
                        case 1:
                            System.out.println("Ingrese las horas que desea estacionar");
                            
                            horas = teclado.nextInt();
                            total = horas * 3;
                            
                            System.out.println("El total de su estacionamiento es de: " + total);
                            
                            dia = dia + total;
                            cont1++;
                          
                        break;
                        
                        case 2:
                            System.out.println("El servicio de media jornada corresponde a 5 hrs");
                            System.out.println("Posee un descuento del 5%");
                            
                            total = 15 - (15 * 0.05);
                            
                            System.out.println("El total de su estacionamiento es de " + total);
                            
                            dia = dia + total;
                            cont2++;
                            
                        break;
                        
                        case 3:
                            System.out.println("El servicio de jornada completa corresponde a 10 hrs");
                            System.out.println("Posee un descuento del 10%");
                            
                            total = 30 - (30 * 0.1);
                            
                            System.out.println("El total de su estacionamiento es de " + total);
                            
                            dia = dia + total;
                            cont3++;
                            
                        break;
                        
                        default:
                            System.out.println("Ingreso un tipo de servicio incorrecto");
                        break;
                    }
                }
            }
            System.out.println("MUCHAS GRACIAS POR SU COMPRA!");
            teclado = new Scanner (System.in);
        }
        System.out.println("Totales monetarios del dia");
        System.out.println("Cantidad de servicios por hora: " + cont1);
        System.out.println("Cantidad de servicios de media jornada: " + cont2);
        System.out.println("Cantidad de servicios de jornada completa: " + cont3);
        System.out.println("El monto total recaudado del dia es de: " + dia);
    }
}